import React, {useState, useRef} from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Popover from '@material-ui/core/Popover';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';

const useStyles = makeStyles((theme) => ({
    typography: {
        padding: theme.spacing(2),
    },
}));

export default function SimplePopover() {
    const userRef = useRef() ;
    const classes = useStyles();
    const [anchorEl, setAnchorEl] = React.useState(null);

    const handleClick = (event) => {
        setAnchorEl(event.currentTarget);
    };

    const handleClose = () => {
        setAnchorEl(null);
    };

    const [searchedUsers, setSearchedUsers] = useState([]);
    const users = ["prasheet", "ambesh", "prashant", "anshul"];
    const handleChange = (e) => {
        // setUsername(e.target.value) ;
        const temp_user = users.filter(user => user.includes(e.target.value));
        console.log(temp_user);
        setSearchedUsers(temp_user);
        if (temp_user.length == 0){
            return handleClose() ;
        }
        
        handleClick(e) ;
        userRef.current.focus() ;
    };

    const open = Boolean(anchorEl);
    const id = open ? 'simple-popover' : undefined;

    return (
        <div>

            <input ref={userRef} onChange={handleChange} placeholder="ente username" />
            <Popover
                id={id}
                open={open}
                anchorEl={anchorEl}
                onClose={handleClose}
                anchorOrigin={{
                    vertical: 'bottom',
                    horizontal: 'center',
                }}
                transformOrigin={{
                    vertical: 'top',
                    horizontal: 'center',
                }}
            >
                <Typography className={classes.typography}>
                    {
                        searchedUsers.map(user => (
                            <p>{user}</p>
                        ))
                    }
                </Typography>
            </Popover>
        </div>
    );
}
