import React, { useEffect, useState } from "react";
import './App.css';
import ComboBox from './ComboBos';

function App() {
  // const [usernam
  const [searchedUsers, setSearchedUsers] = useState([]);
  const [users, setUsers] = useState([]) ;
  const [filter, setFilter] = useState("") ;
  const [notFound, setNotFound] = useState(false);
  // const users = [
  //   {
  //     id : "1",
  //     name : "prasheet",
  //     pin : "324124",
  //     items : "someitem",
  //     address : "addr1"
  //   }, 
  //   {
  //     id : "2",
  //     name : "ambesh",
  //     pin : "324124",
  //     items : "someitem",
  //     address : "addr2"
  //   }, 
  //   {
  //     id : "3",
  //     name : "prasheet1",
  //     pin : "324124",
  //     items : "someitem",
  //     address : "addr3"
  //   }, {
  //     id : "4",
  //     name : "ambesh1",
  //     pin : "324124",
  //     items : "someitem",
  //     address : "addr4"
  //   }
  // ];
  const handleChange = (e) => {
    if (!e.target.value.trim() == "") {
      const temp_user = users.filter(user => user[filter].includes(e.target.value));
      const temp_users = users.filter(user => {
        // for(var key in user){
          return users[key].includes(e.target.value)
          // }
      })

      if (temp_user.length == 0)
        return setNotFound(true);
      setNotFound(false) ;
      console.log(temp_user);
      return setSearchedUsers(temp_user);
    }
    setSearchedUsers([]);
    // setUsername(e.target.value) ;
  };

  const handleOnDropdownChange = (e) => {
    console.log('selected value is : ', e.target.value) ;
    if(e.target.value == "")
      return ;
    setFilter(e.target.value) ;
  }

  useEffect(async() => {
    const temp = await(await fetch("http://localhost:8080/")).json() ;
    console.log('temp : ', temp) ;
    setUsers(temp) ;
  }, []) ;

  return (
    <div className="App">
      <h1>Hello</h1>

      <input onChange={handleChange} placeholder="enter username" />
      <select onChange={handleOnDropdownChange}>
        <option value="">Search By</option>
        <option value='_id'>Id</option>
        <option value='name'>Name</option>
        <option value='address'>Address</option>
        <option value='items'>Items</option>
        <option value='pin'>Pin Code</option>
      </select>
      {
        notFound ?
          <h2>{filter} not found</h2> :
          searchedUsers.map(user => (
            <p className='user-details'>{user[filter]}</p>
          ))
      }
    </div>
  );
}

export default App;
