import React, { useState } from "react";
import './App.css';
import ComboBox from './ComboBos';

function App() {
  // const [usernam
  const [searchedUsers, setSearchedUsers] = useState([]);
  const [notFound, setNotFound] = useState(false);
  const users = ["prasheet", "ambesh", "prashant", "anshul"];
  const handleChange = (e) => {
    if (!e.target.value.trim() == "") {
      const temp_user = users.filter(user => user.includes(e.target.value));
      if (temp_user.length == 0)
        return setNotFound(true);
      setNotFound(false) ;
      console.log(temp_user);
      return setSearchedUsers(temp_user);
    }
    setSearchedUsers([]);

    // setUsername(e.target.value) ;




  };

  const handleOnDropdownChange = (e) => {
    console.log('selected value is : ', e.target.value) ;
  }

  return (
    <div className="App">
      <h1>Hello</h1>

      <input onChange={handleChange} placeholder="ente username" />
      <select onChange={handleOnDropdownChange}>
        <option value='Jabalpur'>Jabalpur</option>
        <option value='Bhopal'>Bhopal</option>
        <option value='Indore'>Indore</option>
      </select>
      {
        notFound ?
          <h2>User not found</h2> :
          searchedUsers.map(user => (
            <p className='user-details'>{user}</p>
          ))
      }
    </div>
  );
}

export default App;
