import React, { useEffect, useState } from "react";
import './App.css';
import ComboBox from './ComboBos';

function App() {
  // const [usernam
  const [searchedUsers, setSearchedUsers] = useState([]);
  const [users, setUsers] = useState([]);
  const [filter, setFilter] = useState("");
  const [notFound, setNotFound] = useState(false);

  const handleChange = (e) => {
    setFilter(e.target.value);
    if (!e.target.value.trim() == "") {
      // const temp_user = users.filter(user => user[filter].includes(e.target.value));
      const temp_user = users.filter(user => {
        // return Object.keys(user).some((key)=>user[key].includes(e.target.value))

        //   for(var x in user){
        //     console.log(` key${x}: value${user[x]}`);
        //     console.log('user',user.name)
        //     if(user[x]===""){
        //       return;
        //     }console.log('x-->',x)
        //     console.log('done-->',user[x].includes(filter))
        //       return user[x].includes(filter)
        // }"['1818192983474', 'prasheet pathak', ]"
        const something = JSON.stringify(Object.values(user));
        console.log('something : ', something);
        if (something.includes(e.target.value))
          return true;
        return false;

      });

      console.log('temp_user', temp_user);

      if (temp_user.length == 0)
        return setNotFound(true);
      setNotFound(false);
      console.log('temp value-->', temp_user);
      return setSearchedUsers(temp_user);
    }
    setSearchedUsers([]);
    // setUsername(e.target.value) ;
  };

  const handleOnDropdownChange = (e) => {
    console.log('selected value is : ', e.target.value) ;
    if(e.target.value == "")
      return ;
    setFilter(e.target.value) ;
  }

  useEffect(async () => {
    const temp = await (await fetch("http://localhost:8080/")).json();
    console.log('temp : ', temp);
    setUsers(temp);
  }, []);

  return (
    <div className="App">
      <h1>Hello</h1>

      <input onChange={handleChange} placeholder="enter username" />
      {/* <select onChange={handleOnDropdownChange}>
         <option value="">Search By</option>
         <option value='_id'>Id</option>
         <option value='name'>Name</option>
         <option value='address'>Address</option>
         <option value='items'>Items</option>
         <option value='pin'>Pin Code</option>
         </select>  */}


         <input onChange={handleChange} placeholder="enter username" />
      <select onChange={handleOnDropdownChange}>
         <option value="">Search By</option>
         <option value='_id'>Id</option>
         <option value='name'>Name</option>
         <option value='address'>Address</option>
         <option value='items'>Items</option>
         <option value='pin'>Pin Code</option>
         </select> 
      {
        notFound ?
          <h2>{filter} not found</h2> :
          searchedUsers.map(user => (
            <div>
              <p className='user-details'>{user._id}</p>
              <p className='user-details'>{user.name}</p>
              <p className='user-details'>{user.address}</p>
              <p className='user-details'>{user.pin}</p>
              <p className='user-details'>{JSON.stringify(user.items)}</p>
            </div>
          ))
      }
    </div>
  );
}

export default App;
